    // Start is called before the first frame update
    void Start() {
        float scale = 0.1f;
        Vector2 offset = new Vector2(-9.5f, -10.5f);

        List<Tuple<Vector2, float>> circlePos = new List<Tuple<Vector2, float>>();
        string[] circleInfo = circles.Replace("\r", "").Split('\n');
        foreach (string c in circleInfo) {
            string[] info = c.Split(',');
            //foreach (string s in info) Debug.Log(s);
            float x = float.Parse(info[0]) * scale * 0.1f;
            float y  = float.Parse(info[1]) * scale * 0.1f;
            float r = float.Parse(info[2]) * scale * 0.58f;
            circlePos.Add(new Tuple<Vector2, float>(new Vector2(x,y) + offset, r));
        }
        foreach (Tuple<Vector2, float> c in circlePos) {
            GameObject newCircle = Instantiate(circlePrefab, c.Item1, Quaternion.identity);
            newCircle.transform.localScale = Vector3.one * c.Item2;
            newCircle.transform.SetParent(transform);
            finalCircles.Add(newCircle);
        }
    }

    // Update is called once per frame
    void Update() {

    }

    [ContextMenu("Reveal All Circles")]
    public void RevealAll() {
        StartCoroutine(ReavealAll());
    }

    IEnumerator ReavealAll() {
        List<GameObject> sCircles = new List<GameObject>(finalCircles);
        sCircles.Sort((a, b) => (int)(a.transform.position.x - b.transform.position.x));
        int i = 0;
        while (i < sCircles.Count) {
            for (int j = 0; j < 4 && i < sCircles.Count; j++, i++) {
                sCircles[i].GetComponent<CircleTile>().Activate();
            }
            yield return null;
        }
    }
